import { Component, OnInit } from '@angular/core';

import { Observable, Subject } from 'rxjs';

import {
   debounceTime, distinctUntilChanged, switchMap
 } from 'rxjs/operators';

import { Pokemon } from '../../pokemon.model';
import { PokemonCRUDService } from '../../pokemon-crud.service';

@Component({
  selector: 'app-pokemon-search',
  templateUrl: './pokemon-search.component.html',
  styleUrls: ['./pokemon-search.component.css']
})
export class PokemonSearchComponent implements OnInit {

  pokemons$!: Observable<Pokemon[]>;
  private searchTerms = new Subject<string>();

  constructor(private pokemonCRUDService: PokemonCRUDService) {}

  // Push a search term into the observable stream.
  search(term: string): void {
    this.searchTerms.next(term);
  }

  onEnter(term: string): void {
    this.searchTerms.next(term);
  }

  enterSubmit(event: any, form: any): void {
    //Enter is 13 

    if (event.keyCode === 13) {
        alert('Enter key is pressed, form will be submitted');
        //calling submit method if key pressed is Enter.
        this.submitit(form);
    } 

     console.log(form.value);
     alert("The form was submitted"); 
     form.reset(); 
  }

  submitit(form: any): void {
    form.submit();
  }

  ngOnInit(): void {
    this.pokemons$ = this.searchTerms.pipe(
      // wait 300ms after each keystroke before considering the term
      debounceTime(300),

      // ignore new term if same as previous term
      distinctUntilChanged(),

      // switch to new search observable each time the term changes
      switchMap((term: string) => this.pokemonCRUDService.searchPokemons(term)),
    );

    // console.log(this.pokemons$);
    // this.pokemons$.forEach(item2 => {
    //   console.log(item2);
    //   // res.push(item.results);
    //   // item.results.forEach(childrenEntry => { // was missing a )
    //   //          list.push({name: childrenEntry.name, url: childrenEntry.url});
    //   // });
    // });
  }






}
