import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Product } from '../../../product.model';
import { ProductService } from '../../../product.service';

@Component({
  selector: 'app-create-product',
  templateUrl: './create-product.component.html',
  styleUrls: ['./create-product.component.css']
})
export class CreateProductComponent implements OnInit {

  product!: Product;
  productAttributes: { 
    name: string; 
    description: string, 
    price: number, 
    phone: number,
    category: string,
    select: string,
    imageUrl: string
  }[] = [];

  cover_photo_for_viewing: any = '';
  cover_photo_for_upload!: File;

  public formDataList = [];

  constructor(
      private fb: FormBuilder,
      private productService: ProductService,
      private router: Router) {
  }

  productForm = this.fb.group({
    items: this.fb.array([]) 
  });

  get items(): any
  {
    let productAttributes = this.productForm.get('items') as FormArray;
    return productAttributes;
  }

  ngOnInit() {
    this.addItem();
  }

  addItem(){
    this.items.push(this.fb.group(
      { 
        name: ['', Validators.required],
        desc: ['', Validators.compose([Validators.required, Validators.minLength(10), Validators.maxLength(1000)])],
        price: ['', Validators.compose([Validators.required])],
        phone: ['', Validators.compose([Validators.required, Validators.minLength(10), Validators.maxLength(10)])],
        category: ['', Validators.compose([Validators.required])],
        select: ['', Validators.compose([Validators.required])],
        imageUrl: ['', Validators.compose([Validators.required])],
      }
    )) 
  } 

  submit()
  {
    if (Array.isArray(this.productForm.value.items)) {
      this.productForm.value.items.forEach( (items: any ) => {
        this.productAttributes.push({
          name: items.name,
          description: items.desc,
          price: items.price,
          phone: items.phone,
          category: items.category,
          select: items.select,
          imageUrl: items.imageUrl
        });
      });
    }

     localStorage.setItem("product", JSON.stringify(this.productAttributes));
     this.resetData();
  }

  get f() {
    return this.productForm.controls;
  }

  resetData() {
    this.productForm.reset();
  }

  addProductClicked(): void {

  }

  coverPhotoPath() {
  }

  productPath() {
  }

  PhotoUrl(i: number, event: any): void {
  }

  readUrl(event: any): void 
  {
    if (event.target.files && event.target.files[0]) {
      const reader = new FileReader();

      reader.onload = (rdr) => {
        this.cover_photo_for_viewing = reader.result;
      };

      reader.readAsDataURL(event.target.files[0]);
      this.cover_photo_for_upload = event.target.files[0];
    }
  }

}
