import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { ActivatedRoute } from '@angular/router';

import { Observable, of } from 'rxjs';
import { Pokemon, PokemonDamage, PokemonEvolution, Pokemons } from '../model/pokemon.model';
import { catchError, map, tap } from 'rxjs/operators';
import { MessageService } from './message.service';
import { POKEMONS_URL, POKEMON_DAMAGE_FIRST_CLASS_URL, POKEMON_DAMAGE_SECOND_CLASS_URL, POKEMON_DAMAGE_THIRD_CLASS_URL, 
  POKEMON_EVOLUTIONS_URL, 
  POKEMON_IMG_URL, POKEMON_SPECIES_URL, POKEMON_TYPE_URL, POKEMON_URL } from '../constants';

@Injectable({
  providedIn: 'root'
})

export class PokemonCRUDService {

  pokemonsDetails: {id: number, name: string; url: string, sprites: any }[] = [];
  previousButton: []=[];
  pokemon?: Pokemon;
  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/json' })
  };

  constructor(private http: HttpClient, private messageService: MessageService, private route: ActivatedRoute) { 
  }

  getPokemons(pokemonsUrl: string): Observable<Pokemon> {
    return this.http.get<Pokemon>(pokemonsUrl);
  }

  getTotalNumberOfPokemons(): Observable<any> 
  {
    let count = 0;
    return this.http.get<Pokemons>(POKEMON_URL)
      .pipe(
        map(pokemonsCount => pokemonsCount['count']), // returns a {0|1} element array
        tap(h => {
          const outcome = h ? 'fetched' : 'did not find';
        }),
        catchError(this.handleError<Pokemon[]>('getTotalNumberOfPokemons', []))
      );
  }

  getSearchedPokemon(term: string): void 
  {
    if (!term.trim()) {
      return;
    }

    this.http.get<Pokemon>(`${POKEMON_URL}/${term}`).subscribe(
      response => {

        if(response){
          window.location.href =  window.location.origin + '/details/' +  `${response.id}`;
        }

      },
      catchError(this.handleError<Pokemon[]>('getSearchedPokemon', []))
    );
  }

  getPokemonTypes() {
    return this.http.get<Pokemon>(POKEMON_TYPE_URL);
  }

  // getPokemonImg(Id: number): any 
  // {
  //   let imgUrl = '';
  //   let no = 0;

  //   this.getTotalNumberOfPokemons().subscribe(
  //     (response) => {
  //       no = response;
  //       //console.log(response);
  //     }
  //   )

  //   this.getPokemon(Id).subscribe(
  //     response => {
  //       for (const [k, v] of Object.entries(response.sprites)) {
  //         if (k === 'front_default') {
  //           imgUrl = v;
  //         }
  //       }
  //     },
  //     catchError(this.handleError<Pokemon[]>('getPokemonIdByName', []))
  //   );

  //   return imgUrl;

  // }

  getPokemonImage(pokemonUrl: string | null): any 
  {

    if(!pokemonUrl){
      pokemonUrl =  POKEMONS_URL;
    }

    this.getPokemons(pokemonUrl).forEach(item => {
      this.previousButton = item.previous
      item.results.forEach(childrenEntry => {
        this.pokemonsDetails.push(
          {
            id: Number(childrenEntry.url.split('/')[childrenEntry.url.split('/').length - 2]),
            name: childrenEntry.name,
            url: childrenEntry.url,
            sprites: POKEMON_IMG_URL + 
              Number(childrenEntry.url.split('/')[childrenEntry.url.split('/').length - 2]) + 
              '.png'
        });
      });
    });

    return this.pokemonsDetails;
  }


  getPokemon(id: number): Observable<Pokemon> 
  {
    const url = `${POKEMON_URL}/${id}`;
    return this.http.get<Pokemon>(url).pipe(
      tap(_ => this.log(`fetched Pokemon id=${id}`)),
      catchError(this.handleError<Pokemon>(`getPokemon id=${id}`))
    );
  }

  getPokemonIdByName(name: string): number | undefined | null 
  {
    if (!name.trim()) {
      return null;
    }

    this.http.get<Pokemon>(`${POKEMON_URL}/${name}`).subscribe(
      response => {
        for (const [k, v] of Object.entries(response)) {
          if (v.name === name) {
            window.location.href = `${POKEMON_URL}/${name}`;

            return v.id;
          }
        }
      },
      catchError(this.handleError<Pokemon[]>('getPokemonIdByName', []))
    );

    return;
  }

  searchPokemons(term: string): Observable<Pokemon[]> 
  {

    if (!term.trim()) {

      return of([]);
    }
    return this.http.get<Pokemon[]>(`${POKEMON_URL}/${term}`).pipe(
      tap(
        (x) => {
          for (const [k, v] of Object.entries(x)) {
            if (v.name === term) {
              window.location.href = POKEMON_URL + term;
            }
          }
        }
      ));
  }

  basicSearch(term: string): any {
    let results = this.http.get<Pokemon>(`${POKEMON_URL}/?name=${term}`);
    return results;
  }

  getPokemonColor(): any 
  {
    let colors = ['aqua', 'black', 'blue', 'fuchsia', 'gray', 'green', 
      'lime', 'maroon', 'navy', 'olive', 'orange', 'purple', 'red', 
      'silver', 'teal', 'white', 'yellow', 'brown', 'magenta', 'kaki'];

    let types: { name: string; color: string}[] = [];

    return this.http.get(POKEMON_TYPE_URL).
      pipe(
        map((data: any) => {
          // console.log(data.results);
          data.results.forEach((itemsV: any, innerIdx: any) =>{
            // console.log(itemsV.name);

            types.push(
                {
                  name: itemsV.name,
                  color: colors[innerIdx]
                }
            );            
          });

          return types;
        }), catchError(this.handleError<Pokemon>(`something went wrong`))
      )
  }

  getMovesForStatusDamageClass() : Observable<PokemonDamage> {
    return this.http.get<PokemonDamage>(POKEMON_DAMAGE_FIRST_CLASS_URL).pipe(
      tap(_ => this.log(`fetched moves for 1st damage class`)),
      catchError(this.handleError<PokemonDamage>(`getMovesForStatusDamageClass`))
    );
  }

  getMovesForPhysicalDamageClass() : Observable<PokemonDamage> {
    return this.http.get<PokemonDamage>(POKEMON_DAMAGE_SECOND_CLASS_URL).pipe(
      tap(_ => this.log(`fetched moves for 1st damage class`)),
      catchError(this.handleError<PokemonDamage>(`getMovesForStatusDamageClass`))
    );
  }

  getMovesForSpecialDamageClass() : Observable<any> {
    return this.http.get<PokemonDamage>(POKEMON_DAMAGE_THIRD_CLASS_URL).pipe(
      tap(_ => this.log(`fetched moves for 1st damage class`)),
      catchError(this.handleError<PokemonDamage>(`getMovesForStatusDamageClass`))
    );
  }

  getPokemonSpecie(id: number): Observable<any> {
    const url = `${POKEMON_SPECIES_URL}/${id}`;
    let pokemonEvolutionId: Number;
    return this.http.get<any>(url)
    .pipe(
      map((data: any) => {
        pokemonEvolutionId =  Number(data.evolution_chain.url.split('/')[data.evolution_chain.url.split('/').length - 2]);

        return pokemonEvolutionId;
      }),  catchError(this.handleError<any>(`getPokemon id=${id}`))
    )
  }

  getEvolutionDetails(id: number): Observable<PokemonEvolution> {
    const url = `${POKEMON_EVOLUTIONS_URL}/${id}`;
    return this.http.get<PokemonEvolution>(url).pipe(
      tap(_ => this.log(`fetched Pokemon evolution id=${id}`)),
      catchError(this.handleError<PokemonEvolution>(`getPokemon id=${id}`))
    );
  }

  /**
   * Handle Http operation that failed.
   * Let the app continue.
   *
   * @param operation - name of the operation that failed
   * @param result - optional value to return as the observable result
   */
  private handleError<T>(operation = 'operation', result?: T) {
    return (error: any): Observable<T> => {

      // TODO: send the error to remote logging infrastructure
      console.error(error); // log to console instead

      // TODO: better job of transforming error for user consumption
      this.log(`${operation} failed: ${error.message}`);

      // Let the app keep running by returning an empty result.
      return of(result as T);
    };
  }

  private log(message: string) {
    this.messageService.add(`PokemonService: ${message}`);
  }
}
