import { Component, OnInit } from '@angular/core';
import { Pokemon } from '../../../model/pokemon.model';
import { ActivatedRoute } from '@angular/router';
import { Location } from '@angular/common';
import { PokemonCRUDService } from '../../../service/pokemon-crud.service';
import { Subject } from 'rxjs/internal/Subject';
import { forkJoin } from 'rxjs/internal/observable/forkJoin';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.css']
})
export class DetailsComponent implements OnInit {

  pokemon: Pokemon | undefined;
  private pokemonBackgroundColor!: string; 
  private pokemonType!: string;
  private test!: number;
  private pokemonAbs: { name: string;}[] = [];
  private pokeAb: any = {};
  pokemonColor!: string;
  pokemonTypes: { name: string; color: string}[] = [];
  private pokemonStatistics: { name: string; base_stat: number;}[] = [];
  private pokemonImg: string | undefined;


  constructor(
    private route: ActivatedRoute,
    private pokemonService: PokemonCRUDService,
    private location: Location
  ) {}

  ngOnInit(): void {
    this.getPokemon();
    this.getPokemonAbilities();
    this.getPokemonStatistics();
    this.getPokCol();
  }

  getPokemonId(): number
  {
    return parseInt(this.route.snapshot.paramMap.get('id')!, 10);
  }

  getPokemon(): void 
  {
    const id = parseInt(this.route.snapshot.paramMap.get('id')!, 10);

    // this.pokemonService.getPokemonAndColors().subscribe( (pokemonTypes: any) => {
    //   console.log(pokemonTypes);

    //   });

     forkJoin([
      this.pokemonService.getPokemon(id),
      this.pokemonService.getPokemonColor(),
    ]).subscribe((res: any) => {
      // console.log(res[1]);
        this.pokemon = res[0];

        res[0].types.forEach((itemsV: any, innerIdx: any) =>{
          this.pokemonTypes.push(
            res[1].find((e: { name: string; }) => e.name === itemsV.type.name)
          );

      });
        // this.pokemonTypes = res[1];
        // this.doSomethingWithFetchedLists();
      });
  }

  getPokemonStatistics()
  {
    const id = parseInt(this.route.snapshot.paramMap.get('id')!, 10);

    this.pokemonService.getPokemon(id)
      .subscribe( (pokemon) => {

        this.pokemonImg = String(pokemon.sprites.front_default)

        pokemon.stats.forEach(items => {
            for (const [key, value] of Object.entries(items['stat'])) { 

               if(key === 'name'){
                 this.pokemonStatistics.push(
                   {
                     name: String(value),
                     base_stat: Number(items['base_stat'])
                   }
               );
              }
            }
        });
     });
  }

  getPokemonAbilities()
  {
    const id = parseInt(this.route.snapshot.paramMap.get('id')!, 10);

    this.pokemonService.getPokemon(id)
      .subscribe( (pokemon) => {

       pokemon.abilities.forEach(items => {
          for (const [key, value] of Object.entries(items['ability'])) { 
            if(key === 'name'){
              this.pokemonAbs.push(
                {
                  name: String(value),
                }
            );
            }
          }
      });
     });
  }

  getPokCol()
  {
    var subject = new Subject<{ name: string; color: string}[]>();
    let pokemonTps: { name: string; color: string}[];

    return subject.asObservable();
  }

  goBack(): void {
    this.location.back();
  }

}
